#How to deal with sidebar and coluns and images
import streamlit as st

st.title("Vote for Chai")

col1,col2 = st.columns(2)

with col1:
    st.header("Masala Chai")
    st.image("data:image/jpeg;base64,",width=200)
    vote1 = st.button("Vote for Masala Chai!") 

with col2:
    st.header("Adrak Chai")
    st.image("data:image/jpeg;base64,",width=200)
    vote2 = st.button("Vote for Adrak Chai!") 



if vote1:
    st.success("Thanks for voting Masala Chai!")

elif vote2:
    st.success("Thnaks for voting Adrak Chai!")

#Sidebar
name = st.sidebar.text_input("Enter your name")
chai = st.sidebar.selectbox("Select your chai",["Masala","Adrak","Kesar"])

st.write(f"{name} ordered {chai} chai")

st.markdown("# What's app buddies!")
st.markdown(" > Hey Buddies")

with st.expander("Ente chai instructions!"):
    st.write("""
1.Boil water with Chai patti \n
2.Add sugar and milk \n
3.Serve Hot           
        """)

